mapper.update:
UPDATE PJ_INFO
SET PJ_STT = #{pjStt}
WHERE PJ_ID =#{pjStt}

UPDATE PJ_INFO
SET VIEW_CNT = VIEW_CNT + 1
WHERE PJ_ID = #{_parameterType}

UPDATE PJ_INDSTR
SET INDSTR_ID = #{indstrId}
WHERE PJ_ID =
#{pjId}

mapper.delete:
DELETE
FROM PJ_APPLY
WHERE PJ_APPLY_ID = #{pjApplyId}
AND
EMIL_ADDR = #{emilAddr}

mapper.update:
UPDATE PJ_INFO
SET OBTN_ID = #{emilAddr}
WHERE PJ_ID =
#{pjId}

mapper.select:
SELECT PJ_ID , ORDR_ID , OBTN_ID , PJ_TTL , RGSTR_DT , STRT_DT , END_DT , CNTRCT_ACCNT , PJ_DESC , IS_DLT , DLT_DT , LST_MOD_DT , IS_RCRUT_ADD , PJ_STT , VIEW_CNT , PJ_RCRUT_CNT , PJ_RCRUT_STRT_DT , PJ_RCRUT_END_DT FROM PJ_INFO WHERE OBTN_ID IN
(SELECT EMIL_ADDR FROM MBR_INFO WHERE CMP_ID =( SELECT CMP_ID FROM MBR_INFO WHERE EMIL_ADDR =#{emilAddr}))
#{emilAddr}

SELECT * FROM PJ_APPLY PA JOIN PJ_INFO PI ON PA.PJ_ID = PI.PJ_ID WHERE PI.PJ_ID IN (SELECT PJ_ID FROM PJ_INFO WHERE PJ_STT = 0 OR PJ_STT =3)
AND PA.EMIL_ADDR IN (SELECT EMIL_ADDR FROM MBR_INFO WHERE CMP_ID = (SELECT CMP_ID FROM MBR_INFO WHERE EMIL_ADDR = 'test@test'))
AND PA.EMIL_ADDR = #{emilAddr}
